import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addMessage, setMessages } from '../store/chatSlice';
import api from '../api'
import { useEffect } from 'react';
import io from 'socket.io-client';
import "./Chat.css"

const socket = io('http://localhost:5000');

const Chat = () => {
    const dispatch = useDispatch();
    const messages = useSelector((state) => state.chat.messages);
    const [input, setInput] = useState('');

    useEffect(()=> {
        socket.on('initialMessages', (existingMessages) =>{
            dispatch(setMessages(existingMessages))
        })
        socket.on('newMessage', (newMessage) => {
            dispatch(addMessage(newMessage))
        })
        return () => {
            socket.off('initialMessages');
            socket.off('newMessage');
        }
    }, [dispatch])

    const sendMessage = () => {
       if (input.trim()) {
        const newMessage = {text: input, sender: 'User'}
        socket.emit('sendMessage', newMessage);
        setInput('');
       }
    };

    return (
        <div className="chat-box">
            <div className="chat-boxi">
                {messages.map((message, index) => (
                    <div key={index} className='chat-message'>
                        <strong style={{ color: '#007BFF' }}>{message.sender}:</strong> {message.text}
                    </div>
                ))}
            </div>

            <div className="chat-messagei">
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={(e) => {
                        if (e.key === 'Enter') {
                            sendMessage();
                        }
                    }}
                    placeholder="Type your message..."
                   className='chat-type'
                />
                <button
                    onClick={sendMessage}
                   className='chat-button'
                >
                    Send
                </button>
            </div>
        </div>
    );
}

export default Chat;
