import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { loginUser, signupUser } from "../redux/authActions"; 

const Login = () => {
  const dispatch = useDispatch();
  const [isSignup, setIsSignup] = useState(false); // Toggle state
  const [formData, setFormData] = useState({ email: "", password: "" });

  // Handle input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (isSignup) {
      dispatch(signupUser(formData)); // Call signup action
    } else {
      dispatch(loginUser(formData)); // Call login action
    }
  };

  return (
    <div>
      <h2>{isSignup ? "Sign Up" : "Login"}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <button type="submit">{isSignup ? "Sign Up" : "Login"}</button>
      </form>

      {/* Toggle between Login and Signup */}
      <p onClick={() => setIsSignup(!isSignup)} style={{ cursor: "pointer", color: "blue" }}>
        {isSignup ? "Already have an account? Login here" : "Don't have an account? Sign up here"}
      </p>
    </div>
  );
};

export default Login;
